// Función para cargar coches en el DOM
function loadCars(carsToLoad, containerId) {
    const container = document.getElementById(containerId);
    if (!container) return; // Si no estamos en la página correcta, salir

    container.innerHTML = ""; // Limpiar contenido previo

    carsToLoad.forEach(car => {
        const carCard = `
            <div class="car-card">
                <img src="${car.image}" alt="${car.make} ${car.model}">
                <div class="car-info">
                    <h3>${car.make} ${car.model}</h3>
                    <p>Tipo: ${car.type}</p>
                    <span class="car-price">$${car.price.toLocaleString()}</span>
                    <a href="contacto.html" class="cta-btn" style="margin-top:10px; width:100%; text-align:center;">Contactar</a>
                </div>
            </div>
        `;
        container.innerHTML += carCard;
    });
}

// Lógica de inicialización según la página
document.addEventListener('DOMContentLoaded', () => {
    
    // Si estamos en Inicio, cargar solo los primeros 3
    if(document.getElementById('featured-cars')) {
        loadCars(carsData.slice(0, 3), 'featured-cars');
    }

    // Si estamos en Catálogo, cargar todos
    if(document.getElementById('catalog-container')) {
        loadCars(carsData, 'catalog-container');
    }

    // Manejo del formulario de contacto
    const contactForm = document.getElementById('contact-form');
    if(contactForm) {
        contactForm.addEventListener('submit', (e) => {
            e.preventDefault();
            alert('¡Gracias! Hemos recibido tu solicitud. Un agente te contactará pronto.');
            contactForm.reset();
        });
    }
});

// Función de filtrado (usada en catalogo.html)
function filterCars(type) {
    const containerId = 'catalog-container';
    
    // Actualizar botones activos
    document.querySelectorAll('.filter-btn').forEach(btn => {
        btn.classList.remove('active');
        if(btn.innerText.includes(type) || (type === 'all' && btn.innerText === 'Todos')) {
            btn.classList.add('active');
        }
    });

    if (type === 'all') {
        loadCars(carsData, containerId);
    } else {
        const filtered = carsData.filter(car => car.type === type);
        loadCars(filtered, containerId);
    }
}